import React, { useState } from "react";
import axios, { Axios } from "axios";

export default function Login() {


  // var y = document.getElementById("myInput");
  // if (y.type === "password") {
  //   y.type = "text";
  // } else {
  //   y.type = "password";
  // }


const url=''

const [data,setData]=useState({
  email:'',
  passwrd:''

})

function submit(e){
  e.preventDefault();
  axios.post(url,{email:data.email,passwrd:data.passwrd})
  .then(res=>{
    console.log(res.data)
  })
}


function handleChange(e){
const newdata={...data}
newdata[e.target.id]=e.target.value
setData(newdata)
console.log(newdata)
}




  return (
    <div>
        <div id="img">
          <img src="/edited.jpg"></img>
        </div>

        <div id="form">
          <form onSubmit={(e)=>submit(e)}>
            <h5 id="formtitle">Fill the Form</h5>

            <div>
              <input
                className="inp"
                type={"email"}
                placeholder="Enter your email"
                onChange={handleChange} value={data.name}
              />
            </div>

            <div>
              <input
                className="inp"
                type={"password"}
                placeholder="Enter your password"
                // id="myInput"
                onChange={handleChange} value={data.passwrd}
              />
            </div>
            <input id="check" type="checkbox" />
            <span>&nbsp; Show Password</span>

            <a href="">
              <h4 className="inp2">Don't have an account sign up?</h4>
            </a>
            <div>
              <button className="logbtn">Log in</button>
            </div>
          </form>
        </div>
      </div>
    );
}










