import React, { Component } from "react";



export default class Login extends Component {
  render() {
    function myFunction() {
      var x = document.getElementById("myInput");
      if (x.type === "password") {
        x.type = "pass";

      } else {
        x.type = "password";
        console.log(x.type)
      }
    }
    return (
      <div>
          <div id="img">
          <img src="/edited.jpg"></img>
        </div>


        <div id="form">
          <form>
            <h5 id="formtitle">Fill the Form</h5>

            <div>
              <input
                className="inp"
                type={"email"}
                placeholder="Enter your email"
              />
            </div>

            <div>
              <input
                className="inp"
                type={"password"}
                placeholder="Enter your password"
                id="myInput"
              />

            </div>
            <input id="check" type="checkbox" onClick={myFunction}/><span>&nbsp; Show Password</span>



            <a href=""><h4 className="inp2">Don't have an account sign up?</h4></a>
            <div>
            <button className="logbtn">Log in</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
