export default class Login extends Component {
  render() {
    function myFunction(event) {
      var x = event.target.v
      console.log(x)
      console.log(x.type)
      console.log(x)
      if (x.type === 'password') {
        x.type = "text";
        console.log(x.type)
      } else {
        x.value = "password";
        console.log(x.type)
      }


    //   var y = document.getElementById("myInput");
