import React, { Component } from "react";

export default class Login extends Component {
  constructor(props) {
    super(props)

    this.state = {
       show:''
    }
  }
  render() {

    function myFunction(z) {
      // var x = event.target.value
      // console.log(x)
      // console.log(x.type)
      // console.log(x)
      // if (x.type === 'password') {
      //   x.type = "text";
      //   console.log(x.type)
      // } else {
      //   x.value = "password";
      //   console.log(x.type)
      // }

      console.log(z)

      var y = document.getElementById("myInput");
      this.set
      if (y.type === "password") {
        y.type = "text";
      } else {
        y.type = "password";
      }
     }


     function handleChange(event){
       var z=event.target
      //  console.log(z)
      //  console.log(typeof(z))
      console.log(z.type)
      this.state.show=''
     }




    return (
      <div>
        <div id="img">
          <img src="/edited.jpg"></img>
        </div>

        <div id="form">
          <form>
            <h5 id="formtitle">Fill the Form</h5>

            <div>
              <input
                className="inp"
                type={"email"}
                placeholder="Enter your email"
              />
            </div>

            <div>
              <input
                className="inp"
                type={"password"}
                placeholder="Enter your password"
                id="myInput"
                onChange={handleChange}
              />
            </div>
            <input id="check" type="checkbox" onClick={myFunction} />
            <span>&nbsp; Show Password</span>

            <a href="">
              <h4 className="inp2">Don't have an account sign up?</h4>
            </a>
            <div>
              <button className="logbtn">Log in</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
