import React, { Component } from 'react'

export default class Login extends Component {
  constructor(props) {
    super(props)

    this.state = {
       mail:'',
       pwd:''
    }
    this.handleChange1= this.handleChange1.bind(this);
    this.handleSubmit= this.handleSubmit.bind(this);
  }



  handleChange1(e){
this.setState({
mail:e
})

  }

  handleSubmit(e){
    e.preventDefault()
var{mail,pwd}=this.state
console.log(mail)
console.log(pwd)
  }




  render() {
    return (
      <div>
      <div id="img">
        <img src="/edited.jpg"></img>
      </div>

      <div id="form">
        <form >
          <h5 id="formtitle">Fill the Form</h5>

          <div>
            <input
            name='em'
              className="inp"
              type={"email"}
              placeholder="Enter your email"
              onChange={this.handleChange1} 
              value={this.state.mail}
            />
          </div>

          <div>
            <input
            name='pw'
              className="inp"
              type={"password"}
              placeholder="Enter your password"
              id="myInput"
              onChange={this.handleChange1} 
              value={this.state.pwd}

            />
          </div>
          <input id="check" type="checkbox" />
          <span>&nbsp; Show Password</span>

          <a href="">
            <h4 className="inp2">Don't have an account sign up?</h4>
          </a>
          <div>
            <button onSubmit={this.handleSubmit} className="logbtn">Log in</button>
          </div>
        </form>
      </div>
    </div>
    )
  }
}






