import React, { Component } from "react";
import axios from "axios";
import { Link,useHistory} from "react-router-dom";



class Login extends Component {
  state = {
    email: "",
    password: "",
    show:false,
    // error:'',
  };



showPassword=(e)=>{
  this.setState({ show: !this.state.show});
}

  onEmail = (e) => {
    this.setState({
      email: e.target.value,
    });
  };

  onPassword = (e) => {

    this.setState({
      password: e.target.value,
      error:e.target.title,
     })


  };



    handleSubmit = (e) => {
      e.preventDefault();
        const data = {
          email: this.state.email,
          password: this.state.password,
        }
      console.log(data);



      console.log(this.state.details)
      axios
        .post("https://jsonplaceholder.typicode.com/posts", data)
        .then((res) => console.log(res))
        .catch((err) => console.log(err));    
    };


 onLogin=()=>{
  if(!this.state.email && this.state.password !=''){
    window.location.href='/Home'
  }
  else{
    alert('please fill the form')
  }
 }




  render()
 {
    return (
      <div className="post">
        <div id="img">
          <img src="/edited.jpg"></img>
        </div>

        <form id="form" onSubmit={this.handleSubmit}>
        <h2 className="heading">Please fill the Form</h2>
          <input
            placeholder="Email"
            value={this.state.email}
            onChange={this.onEmail}
            required
            type={"email"}
            className="inp"
          />

          <input
            id="myInput"
            className="inp"
            type={this.state.show ? "text":"password"}
            placeholder="password"
            value={this.state.password}
            onChange={this.onPassword}
            required
            pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}"
            title="Must contain a number and uppercase and lowercase letter,  8 or more characters"
          />
          {/* <h6 id="err">{this.state.error}</h6> */}


          <input id="check" type="checkbox" onClick={this.showPassword}/>
          <span>&nbsp; Show Password</span>


           <Link to="/Create" ><h4 className="inp2">Don't have an account sign up?</h4></Link> 

          <div>


      <button className="logbtn" onClick={this.onLogin} >Log in</button>



          </div>
        </form>
      </div>
    );
  }
}

export default Login;



