import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


class Create extends Component {
    state = {
    name:'',
      email: "",
      password: "",
      show:false
    };

  showPassword=(e)=>{
    this.setState({ show: !this.state.show});
  }


  onName = (e) => {
    this.setState({
      name: e.target.value,
    });
  };

    onEmail = (e) => {
      this.setState({
        email: e.target.value,
      });
    };

    onPassword = (e) => {
      this.setState({
        password: e.target.value,
      });

    };
    handleSubmit = (e) => {
      e.preventDefault();
      const newdata = {
          name:this.state.name,
        email: this.state.email,
        password: this.state.password,
      };
      console.log(newdata);
      axios
        .post("https://jsonplaceholder.typicode.com/posts", newdata)
        .then((res) => console.log(res))
        .catch((err) => console.log(err));
    };

    render() {
      return (
        <div className="post">
          <div id="img">
            <img src="/edited.jpg"></img>
          </div>

          <form id="form" onSubmit={this.handleSubmit}>

          <input
              placeholder="Name"
              value={this.state.name}
              onChange={this.onName}
              required
              type={"text"}
              className="inp"
            />


            <input
              placeholder="Email"
              value={this.state.email}
              onChange={this.onEmail}
              required
              type={"email"}
              className="inp"
            />

            <input
              id="myInput"
              className="inp"
              type={this.state.show ? "text":"password"}
              placeholder="password"
              value={this.state.password}
              onChange={this.onPassword}
              required
            />

<select  className="inpselect">
    <option>a</option>
    <option>b</option>
    <option>c</option>
</select>


            <input id="check" type="checkbox" onClick={this.showPassword}/>
            <span>&nbsp; Show Password</span>


            <div>
              <button className="logbtn">Sign in</button>
            </div>
          </form>
        </div>
      );
    }
  }

  export default Create;
