import React, { Component } from "react";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: 0,
    };




 }


  Increment = () => {
    this.setState((prev) => {
      return {
        product: prev.product + 1,
      };
    });
  };

  Decrement = () => {
    this.setState((prev) => {
      return {
        product: prev.product - 1,
      };
    });
  };

  render() {
      let prod1=this.state.product
      console.log(prod1)
    return (
      <div>
{/* 
<div>
    <img src="image1.jpeg"></img>
</div> */}

        <div>
          <button onClick={this.Increment}>+</button>
          <span>Qty {this.state.product}</span>
          <button onClick={this.Decrement}>-</button>
          <div>
          <button>Add to cart</button>
          </div>

          <h1>{prod1}</h1>

         <div><RenderingArrayOfObjects/></div>
        </div>
      </div>
    );
  }
}



// import React from 'react'
function RenderingArrayOfObjects() {
    const data = [
        {
            "State": "Uttar Pradesh",
            "Capital": "Lucknow"
        },
        {
            "State": "Gujarat",
            "Capital": "Gandhinagar"
        },
        {
            "State": "Karnataka",
            "Capital": "Bengaluru"
        },
        {
            "State": "Punjab",
            "Capital": "Chandigarh"
        },
        {
            "State": "Maharashtra",
            "Capital": "Mumbai"
        }
    ]
    const listItems = data.map(
        (element) => {
            return (
                <ul type="disc">
                    <li style={{ 
                        fontWeight: 'bold', 
                        color: 'red' }}
                    >
                        {element.State}
                    </li>
                    <li>{element.Capital}</li>
                </ul>
            )
        }
    )
    return (
        <div>
            {listItems}
        </div>
    )
}
















