  const onClick = (e) => {
    let x=e.target
      updateMyArray([...myArray, x]);
  };
  return [
      <div>
<button onClick={ onClick() }></button>
      <div>{myArray.map( e =>
        <div>{ e }</div>

      )}
      </div>
      </div>
  ];
}

export default App;





