import React, { Component } from 'react'
import Select from 'react-select'
import axios from 'axios'
import "./App.css";

export default class Dropdown extends Component {
  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      name: '',
      email:''
    }
  }

  componentDidMount() { }



  handleChange(e){
   this.setState({id:e.value, name:e.label,email:e.mail})
  }

  // componentDidMount(){
  //     this.getOptions()
  // }

  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
        <Select className="inpselect" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
    <p>You have selected <strong>{this.state.name}</strong> whose id is <strong>{this.state.id}</strong>and name is<strong>{this.state.email}</strong></p>
      </div>
    )
  }
}
