import React, { Component } from "react";
import { Button, Form, InputGroup } from "react-bootstrap";

export default class Login extends Component {
  render() {
    function myFunction(event) {
      var x = event.targ
      if (x.type === "password") {
        x.type = "text";
      } else {
        x.type = "password";
      }
    }
    return (
      <div>
          <div id="img">
          <img src="/edited.jpg"></img>
        </div>


        <div id="form">
          <form>
            <h5 id="formtitle">Fill the Form</h5>

            <div>
              <input
                className="inp"
                type={"email"}
                placeholder="Enter your email"
              />
            </div>

            <div>
              <input
                className="inp"
                type={"password"}
                placeholder="Enter your password"
                value="FakePSW"
                id="myInput"
              />
              <input type="checkbox" onClick={myFunction}/><span>Show Password</span>
            </div>



            <a href=""><h4 className="inp2">Don't have an account sign up?</h4></a>
            <div>
            <button className="logbtn">Log in</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
