import React, { Component } from "react";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: 0,
    };



 }

 componentDidMount() { 
  const images = [
    { id: 1, src: 'image1.jpeg', title: 'one', description: '1' },
    { id: 2, src: 'image2.jpeg', title: 'two', description: '2' },
    { id: 3, src: 'image3.jpeg', title: 'three', description: '3' },
    { id: 4, src: 'image4.jpeg', title: 'four', description: '4' },

  ];

  }


  Increment = () => {
    this.setState((prev) => {
      return {
        product: prev.product + 1,
      };
    });
  };

  Decrement = () => {
    this.setState((prev) => {
      return {
        product: prev.product - 1,
      };
    });
  };

  render() {
      let prod1=this.state.product
      console.log(prod1)
    return (
      <div>
{/* 
<div>
    <img src="image1.jpeg"></img>
</div> */}

        <div>
          <button onClick={this.Increment}>+</button>
          <span>Qty {this.state.product}</span>
          <button onClick={this.Decrement}>-</button>
          <div>
          <button>Add to cart</button>
          </div>

          <h1>{prod1}</h1>

         <div> { images.map(({id, src, title, description}) => <img key={id} src={src} title={title} alt={description} />)

}</div>
        </div>
      </div>
    );
  }
}



