export default class Home extends Component {
    constructor(props) {
      super(props)

      this.state = {
        product:0,
res:''
      }
    }

    Increment=()=>{
   this.setState({
       product:this.state.product+1
   })
    }

    Decrement=()=>{
        this.setState((prev)=>{
            return{
                product:prev.product-1
            }
        })
    }


    handleChangeinp=(e)=>{
let out=e.target.value
this.setState({
res:out
})
    }



  render() {
    return (
      <div>


<div>
<button onClick={this.Increment}>+</button>
<input onChange={this.handleChangeinp} value={this.state.product}/>
<button onClick={this.Decrement}>-</button>
<h1>ok{this.state.res}</h1>
</div>

      </div>
    )
  }
}
