import React, { useState } from "react";
import axios from "axios";

export default function Login() {





const url=''

const [data,setData]=useState({
  email:'',
  password:''
})


function handleChange(e){
const newdata={...data}
newdata[e.target.id]=e.target.value
setData(newdata)
console.log(newdata)
}




  return (
    <div>
        <div id="img">
          <img src="/edited.jpg"></img>
        </div>

        <div id="form">
          <form>
            <h5 id="formtitle">Fill the Form</h5>

            <div>
              <input
                className="inp"
                type={"email"}
                placeholder="Enter your email"
                onChange={(e)=>handleChange(e)} value={data.name}
              />
            </div>

            <div>
              <input
                className="inp"
                type={"password"}
                placeholder="Enter your password"
                id="myInput"
                onChange={(e)=>handleChange(e)} value={data.password}
              />
            </div>
           <Remember/>
            <a href="">
              <h4 className="inp2">Don't have an account sign up?</h4>
            </a>
            <div>
              <button className="logbtn">Log in</button>
            </div>
          </form>
        </div>
      </div>
    );
}



 class Remember extends Component {
  componentDidMount() { 
    var y = document.getElementById("myInput");
    if (y.type === "password") {
      y.type = "text";
    } else {
      y.type = "password";
    }
   }
  render() {
    return (
      <div>
         <input id="check" type="checkbox" />
            <span>&nbsp; Show Password</span>

      </div>
    )
  }
}










