import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Data from "./Data";

class Login extends Component {
  state = {
    email: "",
    password: "",
    show: false,
    fields: {},
    errors: {},
  };

  showPassword = (e) => {
    this.setState({ show: !this.state.show });
  };

  onEmail = (e) => {
    this.setState({
      email: e.target.value,
    });
  };

  onPassword = (e) => {
    this.setState({
      password: e.target.value,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      email: this.state.email,
      password: this.state.password,
    };
    console.log(data);
    axios
      .post("https://jsonplaceholder.typicode.com/posts", data)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div className="post">
        {/* <div id="img">
          <img src="/edited.jpg"></img>
        </div> */}

        <form id="form" onSubmit={this.handleSubmit}>
          <h2 className="heading">Please fill the Form</h2>
          <input
            placeholder="Email"
            value={this.state.fields.email}
            onChange={this.onEmail}
            required
            type={"email"}
            className="inp"
          />

          <input
            id="myInput"
            className="inp"
            type={this.state.show ? "text" : "password"}
            placeholder="password"
            value={this.state.fieldspassword}
            onChange={this.onPassword}
            required
          />

          <input id="check" type="checkbox" onClick={this.showPassword} />
          <span>&nbsp; Show Password</span>

          <Link to="/Create">
            <h4 className="inp2">Don't have an account sign up?</h4>
          </Link>

          <div>
            <button className="logbtn">Log in</button>
            <Data />
          </div>
        </form>
      </div>
    );
  }
}

export default Login;

// https://api.first.org/data/v1/countries
