import React, { Component } from "react";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: 0,
    };



    let datas=[
      {
      "type": "book1",
      "image": "image1.jpeg"
       }
  ,
       {
        "type": "book2",
        "image": "image2.jpeg"
         }


         ,
       {
        "type": "book3",
        "image": "image3.jpeg"
         }

         ,
         {
          "type": "book4",
          "image": "image4.jpeg"
           }

        ]

  }




  Increment = () => {
    this.setState((prev) => {
      return {
        product: prev.product + 1,
      };
    });
  };

  Decrement = () => {
    this.setState((prev) => {
      return {
        product: prev.product - 1,
      };
    });
  };

  render() {
      let prod1=this.state.product
      console.log(prod1)
    return (
      <div>ay of objects in react class component

<div>
    <img src="image1.jpeg"></img>
</div>

        <div>
          <button onClick={this.Increment}>+</button>
          <span>Qty {this.state.product}</span>
          <button onClick={this.Decrement}>-</button>
          <div>
          <button>Add to cart</button>
          </div>

          <h1>{prod1}</h1>

          <h1>{datas.map(e=>{
          <p>{e.datas}</p>
          })}</h1>
        </div>
      </div>
    );
  }
}
