import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


class Create extends Component {
    state = {
      email: "",
      password: "",
      show:false
    };

  showPassword=(e)=>{
    this.setState({ show: !this.state.show});
  }

    onEmail = (e) => {
      this.setState({
        email: e.target.value,
      });
    };

    onPassword = (e) => {
      this.setState({
        password: e.target.value,
      });

    };
    handleSubmit = (e) => {
      e.preventDefault();
      const newdata = {
        email: this.state.email,
        password: this.state.password,
      };
      console.log(newdata);
      axios
        .post("https://jsonplaceholder.typicode.com/posts", newdata)
        .then((res) => console.log(res))
        .catch((err) => console.log(err));
    };

    render() {
      return (
        <div className="post">
          <div id="img">
            <img src="/edited.jpg"></img>
          </div>

          <form id="form" onSubmit={this.handleSubmit}>

          <input
              placeholder="Name"
              value={this.state.name}
              onChange={this.onName}
              required
              type={"email"}
              className="inp"
            />


            <input
              placeholder="Email"
              value={this.state.email}
              onChange={this.onEmail}
              required
              type={"email"}
              className="inp"
            />

            <input
              id="myInput"
              className="inp"
              type={this.state.show ? "text":"password"}
              placeholder="password"
              value={this.state.password}
              onChange={this.onPassword}
              required
            />
               <input
              id="myInput"
              className="inp"
              type={this.state.show ? "text":"password"}
              placeholder="password"
              value={this.state.password}
              onChange={this.onPassword}
              required
            />

            <input id="check" type="checkbox" onClick={this.showPassword}/>
            <span>&nbsp; Show Password</span>

            <a href="">
             <Link to="/Create" ><h4 className="inp2">Don't have an account sign up?</h4></Link> 
            </a>
            <div>
              <button className="logbtn">Log in</button>
            </div>
          </form>
        </div>
      );
    }
  }

  export default Create;
