import { event } from "jquery";
import React, { Component } from "react";
import "./App.css";
export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: 0,
      prod1: 0,
      cart:[],
      alld:''
    };
  }

  Increment = () => {
    if (this.state.product < 10) {
      // console.log(this.state.product);
      this.setState((prev) => {
        return {
          product: prev.product + 1,
        };
      });
    } else {
      alert("maximum 10 products allowed!");
    }
  };

  Decrement = () => {
    if (this.state.product > 0) {
      this.setState((prev) => {
        return {
          product: prev.product - 1,
        };
      });
    } else {
      alert("Products should be greater than 0");
    }
  };

  addCartbtn = (e) => {
    this.setState({
      cart:this.state.product

    })
    if (this.state.product > 0) {
      this.setState({ prod1: this.state.product });
      if (this.state.product == 1) {
        alert(`${this.state.product} product added sucessfully`);
      } else if (this.state.product > 1) {
        alert(`${this.state.product} products added sucessfully`);
      }
    } else {
      alert("Please select the products");
    }
  };

  render() {
    // console.log(prod1);
    return (
      <div>
        {/* 
<div>
    <img src="image1.jpeg"></img>
</div> */}

        <div>
          <p className="cart">Items in cart [{this.state.prod1}]</p>
          <p>{this.state.cart}</p>
          <div id="images">

            {images.map(({ src, title,id }) =>(

              <div key={id}>

                <img id="imagess" src={src} />
                <br />
                <p className="prodnames">{title}</p>

                <button className="indecbtn" onClick={this.Decrement}>
                  -
                </button>
                <span className="prodnames">Qty {this.state.product}</span>
                <button className="indecbtn" onClick={this.Increment}>
                  +
                </button>
                <div>
                  <button onClick={this.addCartbtn} id="adcbtn">
                    Add to cart
                  </button>
                </div>
              </div>
            ))}
          </div>
          <div>
 <button className="indecbtn" onClick={this.Decrement}>
                  -
                </button>
                <span className="prodnames">Qty {this.state.product}</span>
                <button className="indecbtn" onClick={this.Increment}>
                  +
                </button>
                <div>
                  <button onClick={this.addCartbtn} id="adcbtn">
                    Add to cart
                  </button>
                </div> 








                </div>





        </div>

        <div></div>
      </div>
    );
  }
}

