import React, { Component } from "react";
import "./App.css";
export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      product: 0,

      prod1: 0,
    };
  }

  Increment = () => {
    if (this.state.product < 10) {
      // console.log(this.state.product);
      this.setState((prev) => {
        return {
          product: prev.product + 1,
        };
      });
    } else {
      alert("maximum 10 products allowed!");
    }
  };

  Decrement = () => {
    if (this.state.product > 0) {
      this.setState((prev) => {
        return {
          product: prev.product - 1,
        };
      });
    } else {
      alert("Products should be greater than 0");
    }
  };

  addCartbtn = () => {
    if (this.state.product > 0) {
      this.setState({ prod1: this.state.product });
    }
  };

  render() {
    // console.log(prod1);
    return (
      <div>
        {/* 
<div>
    <img src="image1.jpeg"></img>
</div> */}

        <div>
          <h1>{this.state.prod1}</h1>

          <div id="images">
            {images.map(({ src },{one}) => (
              <div>
                <img src={src} />
                <br />
                <button className="indecbtn" onClick={this.Increment}>
                  +
                </button>
                <span>Qty {this.state.product}</span>
                <button className="indecbtn" onClick={this.Decrement}>
                  -
                </button>
                <div>
                  <button onClick={this.addCartbtn} id="adcbtn">
                    Add to cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div></div>
      </div>
    );
  }
}

const images = [
  { id: 1, src: "image1.jpeg", title: "one", description: "1" },
  { id: 2, src: "image2.jpeg", title: "two", description: "2" },
  { id: 3, src: "image3.jpeg", title: "three", description: "3" },
  // { id: 4, src: 'image4.jpeg', title: 'four', description: '4' },
];
